import React from "react";

const CampaignList = ({ campaigns, onUpdate, onDelete }) => (
  <table border="1">
    <thead><tr><th>Name</th><th>Client</th><th>Start Date</th><th>Status</th><th>Actions</th></tr></thead>
    <tbody>
      {campaigns.map(c => (
        <tr key={c.id}>
          <td>{c.campaignName}</td>
          <td>{c.clientName}</td>
          <td>{c.startDate}</td>
          <td>{c.status}</td>
          <td>
            <select value={c.status} onChange={e => onUpdate(c.id, e.target.value)}>
              <option>Active</option><option>Paused</option><option>Completed</option>
            </select>
            <button onClick={() => onDelete(c.id)}>Delete</button>
          </td>
        </tr>
      ))}
    </tbody>
  </table>
);

export default CampaignList;
