import axios from "axios";

const API_URL = "http://localhost:5000/api/campaigns";

export const getCampaigns = () => axios.get(API_URL);
export const addCampaign = (data) => axios.post(API_URL, data);
export const updateCampaignStatus = (id, status) => axios.put(`${API_URL}/${id}`, { status });
export const deleteCampaign = (id) => axios.delete(`${API_URL}/${id}`);
